package com.orange.intorduction;



public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Primitive Data Type	
		/*
		 * byte Data Type
		 * Range : - 128 to 128 (8 bits size)
		 * Default 0
		 */
		
		byte MYBYTE_VARIABLE = 100;
		System.out.println("Value of mybyteVariable : " + MYBYTE_VARIABLE);
	
		
		/*
		 * short Data Type
		 * Range : -32,768 - 32,768
		 * Default 0
		 */
		
		short MYSHORT_VARIABLE = 32000;
		System.out.println("Value of myShortVariable : " + MYSHORT_VARIABLE);
		
		/*
		 * int Data Type
		 * Range : -2,147,483,648  - 2,147,483,648
		 * Default 0
		 */
		
		int MYINT_VARIABLE = 999999;
		System.out.println("Value of myIntVariable : " + MYINT_VARIABLE);

		/*
		 * long Data Type
		 * Range : 
		 * Default 0
		 */
		
		long MYLONG_VARIABLE = 987654321L;
		System.out.println("Value of myLongVariable : " + MYLONG_VARIABLE);
		
		/*
		 * float Data Type
		 * Range : 
		 * Default 
		 */
		
		float MYFLOAT_VARIABLE = 10.2f;
		System.out.println("Value of myFloatVariable : " + MYFLOAT_VARIABLE);
		
		 /*
		 * double Data Type
		 * Range :
		 * 
		 */
		double MYDOUBLE_VARIABLE = 10.23455;
		System.out.println("Value of myDoubleVariable : " + MYDOUBLE_VARIABLE);
		
		/*
		 * char
		 */
		char MYCHAR_VARIABLE = 'k';
		System.out.println("Value of myCharVariable : " + MYCHAR_VARIABLE);
		
		/*
		 * boolean 
		 */
		
		boolean MYBOOLEAN_VARIABLE = true;
		System.out.println("Value of myBooleanVariable : " + MYBOOLEAN_VARIABLE);
	}

}
